func solve(input string, v bool) (part1TotalScore, part2TotalScore int) {
	lines := strings.Split(input, "\n")

	//lines []string
	//line string
	var err error
	for i := 0; i < len(lines); i++ {
		maxLen := len(lines[i])
		for j := 0; j < maxLen; j++ {
			if (lines[i][j] == '(') && (lines[i][j+1] == ')') {
				lines, err = utils.Remove(lines, j)
				utils.CheckErr(err)
				//lines[i] = append(line[:j], lines[i][j+1:]...)
			}
		}
		utils.Log(v, "info", lines[i])
	}

	return part1TotalScore, part2TotalScore
}

