package main

import (
	"github.com/adrien3d/adventofcode/utils"
	"strconv"
	"strings"
)

func solve(input string, v bool) (part1TotalScore, part2TotalScore int) {
	lines := strings.Split(input, "\n\n")

	var caloriesArray []int
	top3Calories := make([]int, 3)
	for _, line := range lines {
		var reinderCalorie int
		caloriesStrArray := strings.Split(line, "\n")
		for _, calorie := range caloriesStrArray {
			cal, _ := strconv.Atoi(calorie)
			reinderCalorie += cal
		}
		caloriesArray = append(caloriesArray, reinderCalorie)

		newCalToInsert := -1
		if (reinderCalorie > top3Calories[0]) && (reinderCalorie > top3Calories[1]) && (reinderCalorie > top3Calories[2]) {
			newCalToInsert = 0
		} else if (reinderCalorie < top3Calories[0]) && (reinderCalorie > top3Calories[1]) && (reinderCalorie > top3Calories[2]) {
			newCalToInsert = 1
		} else if (reinderCalorie < top3Calories[0]) && (reinderCalorie < top3Calories[1]) && (reinderCalorie > top3Calories[2]) {
			newCalToInsert = 2
		}
		if newCalToInsert != -1 {
			if newCalToInsert == 0 {
				if top3Calories[0] != 0 {
					top3Calories[1] = top3Calories[0]
				}
				if top3Calories[1] != 0 {
					top3Calories[2] = top3Calories[1]
				}
			}
			if newCalToInsert == 1 && top3Calories[1] != 0 {
				top3Calories[2] = top3Calories[1]
			}
			top3Calories[newCalToInsert] = reinderCalorie
		}
		utils.Log(v, "info", top3Calories)
	}
	var total int
	for _, cal := range top3Calories {
		total += cal
	}
	utils.Log(v, "info", caloriesArray)
	utils.Log(v, "info", top3Calories)
	utils.Log(v, "info", total)

	return part1TotalScore, part2TotalScore
}

