package main

import (
	"github.com/adrien3d/adventofcode/utils"
)

func solvePart(input string, patternLength int) int {
	for i := 0; i < len(input)-(patternLength-1); i++ {
		conditions := true
		for j := 0; j <= patternLength-1; j++ {
			var strRange []uint8
			for k := 0; k < patternLength; k++ { //we want to avoid to include current char in array (to be able to find it)
				if k != j {
					strRange = append(strRange, input[i+k])
				}
			}
			if utils.UInt8ArrayContains(strRange, input[i+j]) {
				conditions = false
			}
		}
		if conditions == true {
			return i + patternLength
		}
	}
	return 0
}

func solve(input string, v bool) (part1TotalScore, part2TotalScore int) {
	return solvePart(input, 4), solvePart(input, 14)
}

func main() {
	testInput := "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
	realInput := ""
	utils.Run(solve, testInput, true)
	utils.Run(solve, realInput, true)
}
