package main

import (
	"fmt"
	"github.com/adrien3d/adventofcode/utils"
	"path"
	"strings"
	"unicode"
)

func solve(input string, v bool) (part1TotalScore, part2TotalScore int) {
	//Step 1: Mapping files
	fs, cd := map[string]int{}, ""
	for _, str := range strings.Split(strings.TrimSpace(input), "\n") {
		if strings.HasPrefix(str, "$ cd") {
			cd = path.Join(cd, strings.Fields(str)[2])
		} else if unicode.IsDigit([]rune(str)[0]) { //file
			var size int
			var name string
			fmt.Sscanf(str, "%d %s", &size, &name)
			fs[path.Join(cd, name)] = size
		}
	}

	//Step 2: calculating directories sizes
	ds := map[string]int{}
	for f, s := range fs {
		for d := path.Dir(f); d != "/"; d = path.Dir(d) {
			ds[d] += s
		}
		ds["/"] += s
	}

	part2TotalScore = ds["/"]
	for _, s := range ds {
		if s <= 100000 {
			part1TotalScore += s
		}
		if (70000000-ds["/"]+s >= 30000000) && (s < part2TotalScore) {
			part2TotalScore = s
		}
	}

	return part1TotalScore, part2TotalScore
}

